INSERT INTO configuration ( configuration_title, configuration_key, configuration_value, configuration_description, configuration_group_id, sort_order, date_added, use_function, set_function)
VALUES ( "Enable the notifier-trace for your store?", "NOTIFIER_TRACE_STORE", '', "You can disable the notifier-trace for your store by setting this value to blank (the default). To enable the notifier-trace for <b>all</b> customers, enter <span style=\"color:red;\">*</span>, to enable the trace for a <em>specific</em> customer, enter that customer's <em>customer_id</em> value or to enable the trace <em>only</em> for a specific IP address, enter that value here.<br /><br />The information is stored in the file \"notifier_trace.log\" in the store\'s logs directory for Zen Cart v1.5.1 and later or the cache directory otherwise.", 10, 10, NOW(), NULL, NULL );
  
INSERT INTO configuration ( configuration_title, configuration_key, configuration_value, configuration_description, configuration_group_id, sort_order, date_added, use_function, set_function )
VALUES ( 'Enable the notifier-trace for your admin?', 'NOTIFIER_TRACE_ADMIN', '', "You can disable the notifier-trace for your admin by setting this value to blank (the default). To enable the notifier-trace for <b>all</b> admin-level accesses, enter <span style=\"color:red;\">*</span>, to enable the trace for a <em>specific</em> admin, enter that admin's <em>admin_id</em> value or to enable the trace <em>only</em> for a specific IP address, enter that value here..<br /><br />The information is stored in the file \"notifier_trace.log\" in the store\'s logs directory for Zen Cart v1.5.1 and later or the cache directory otherwise.", 10, 11, NOW(), NULL, NULL );

DELETE FROM configuration WHERE configuration_key = 'NOTIFIER_TRACE';